import { React, Component } from 'react';


import './styles/Cart.css';


class Cart extends Component {
      constructor(props) {
        super(props);
      }
    render() {

      const cart = this.props.selectedProduct.map((products, index) => (
        
        <div key={index} className='cart'>
        <div className='cart-titles'>
          <h1>{products.name}</h1>
          <h2>{products.brand}</h2>
          <h3><span>{this.props.currency}</span> {console.log(products)} {products.prices.map((cur, r) => (
                  this.props.currency === cur.currency.symbol ? console.log(cur.amount) : ""
                ))}</h3>
          <div className='sizes-btn'>
            <button className='btn-hover btn-size'>S</button>
            <button className='btn-hover btn-size'>M</button>
          </div>
          </div>
        <div className='cart-imgs'>
          <div className='cart-counters'>
            <button className='btn-hover'>+</button>
            <p>1</p>
            <button className='btn-hover'>-</button>
          </div>
          <img alt='' src={products.gallery[0]}></img> 
        </div>
  </div>
      ))
      return (

        <div className='cart-container'>
          <div className='heading-container'>
          <h1>CART</h1>
          </div>
          {cart}
        </div>
      ) 
    }
  }

  export default Cart;
