import { React, Component } from 'react';


import './styles/Cart.css';


class Cart extends Component {
      constructor(props) {
        super(props);
      }
    render() {
      let i = 0;
      const cart = this.props.selectedProduct.map((index, products) => (
          products[index].name
      ))
      return (

        <div className='cart-container'>
          <div className='heading-container'>
          <h1>CART</h1>
          </div>
          <div className='cart'>
            <div className='cart-titles'>
              <h1>{console.log(this.props.selectedProduct)}</h1>
              <h2>{this.props.selectedProduct[i].brand}</h2>
              <h3>${this.props.selectedProduct[i].prices[0].amount}</h3>
              <div className='sizes-btn'>
                <button className='btn-hover btn-size'>S</button>
                <button className='btn-hover btn-size'>M</button>
              </div>
              </div>
            <div className='cart-imgs'>
              <div className='cart-counters'>
                <button className='btn-hover'>+</button>
                <p>1</p>
                <button className='btn-hover'>-</button>
              </div>
              <img alt='' src={this.props.selectedProduct[i].gallery[0]}></img> 
            </div>
      </div>
      {cart}
        </div>
      ) 
    }
  }

  export default Cart;
