import { React, PureComponent } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Content from "./component/content/Content";
import Header from "./component/header/Header";
import Home from "./component/home/Home";
import PDP from "./component/pdp/PDP";
import CardOverlay from "./component/card-overlay/CardOverlay";
import Cart from './component/cart/Cart';

class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
        pickedColor: "",
        pickedCapacity: "",
        pickedSize: "",
        currentCurrency: "$",
        productCategory: "/all",
        productDetail: [],
        openClose: false,
        qty: '',
        product: [

        ],
        nextProductId: 0,
    }
    this.changeCurrency = this.changeCurrency.bind(this);
    this.setLink = this.setLink.bind(this);
    this.productDetails = this.productDetails.bind(this);
    this.openCloseCard = this.openCloseCard.bind(this);
    this.closeCard = this.closeCard.bind(this);
    this.addToCart = this.addToCart.bind(this);
    this.onDelete = this.onDelete.bind(this);
    this.handleAttributPick = this.handleAttributPick.bind(this);
  }
  changeCurrency(currency) {
    this.setState({currentCurrency: currency})
  }
  setLink(link) {
      this.setState({productCategory: link})
  }
  productDetails(details) {
    this.setState({productDetail: details})
  }
  openCloseCard(bool) {
    this.setState({openClose: !bool})
  }
  closeCard(close) {
    this.setState({openClose: close})
  }
  addToCart(item) {
    this.setState((prevState) => {
      const newItem = {...item, itemId: this.state.nextProductId, qty: 1, color: this.state.pickedColor};
      return {
        nextProductId: prevState.nextProductId + 1,
        product: [...this.state.product, newItem]
      }
    })
  }
  onDelete(index) {
    var array = this.state.product.slice();
      array.splice(index, 1);
      this.setState({product: array})  

  }

  handleAttributColor(attr) {
    this.setState({pickedAttribute: color = attr})
  }
  handleAttributSize(attr) {
    this.setState({pickedAttribute: size = attr})
  }
  handleAttributCapacity(attr) {
    this.setState({pickedAttribute: capacity = attr})
  }
  render() {
    return (
      <div className="App"> 
              <Router className="router">
              <Header
                onChangeCurrency = {this.changeCurrency}
                setLink = {this.setLink}
                openClose = {this.openCloseCard}
                productCount = {this.state.product.length}
                />
              {this.state.openClose === true ? <CardOverlay
                closeCard = {this.closeCard}
                toCart = {this.state.product}
                currency = {this.state.currentCurrency}
                  /> : ''}
                <Routes>
                  <Route path="/" exact={true}
                   element= {<Home />}
                  />

                <Route path={this.state.productCategory}
                  element= {<Content cat = {this.state.productCategory} 
                  productDetail ={this.productDetails}
                  currency = {this.state.currentCurrency} />} 
                    />

                <Route path={this.state.productCategory + "/" + this.state.productDetail.id}  
                  element= {<PDP
                  productDetails = {this.state.productDetail}
                  selectedProduct = {this.addToCart}
                  currency = {this.state.currentCurrency}
                  handleAttributColor = {this.handleAttributColor}
                  handleAttributCapacity = {this.handleAttributCapacity}
                  handleAttributCapacity = {this.handleAttributCapacity}
                  />} 
                  />

                <Route path="cart"
                  element= {<Cart 
                  selectedProduct = {this.state.product}
                  currency = {this.state.currentCurrency}
                  delete = {this.onDelete}
                  />} 
                  />
                </Routes>
            </Router>
      </div>
    );
  }

}

export default App;
