import { React, Component } from 'react';

import './styles/CardOverlay.css';
import { NavLink } from 'react-router-dom';

class CardOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cardOption: false,
    }
    this.handleClick = this.handleClick.bind(this);
  }
  handleClick(type, num) {
    this.setState(() => {
      return {num: type = 'add' ? num + 1 : num -1 }
    })
  }
  
    render() {
        const items = this.props.toCart.map((item, index) => (
            <div key={index} className='card'>
            <div className='card-description'>
                <p>{item.name}</p>
                <p>{item.brand}</p>
                <p>{item.prices[0].amount}</p>
                <div className='size-btn-container'>
                    <button className='card-btn size-btn'>S</button>
                    <button className='card-btn size-btn'>M</button>
                </div>
            </div>
            <div className='plus-btn'>
                <button onClick={() => this.props.setState({qty: item.qty + 1 })} className='card-btn'>+</button>
                <span>{item.qty }</span>
                <button onClick={() => this.props.toCart.setState({qty: item.qty - 1 })} className='card-btn'>-</button>
            </div>
            <div className='card-photo'>
                <img alt='' src={item.gallery[0]} />
            </div>
        </div>
          
        ))
      return (
          <div className='card-container'>
            <div onClick={() => this.props.closeCard(false)} className='card'>

            </div>
            <div className='cover-overlay'>
                <div>
                    <p>My Bag. <span>{this.props.toCart.length} items</span></p>
                </div>

                {items}

                <div className='check-container'>
                      <NavLink to="cart"><button className='transparent'>view bag</button></NavLink>
                      <button className='green-white'>check out</button>
                </div>
             </div>
          </div>
      ) 
    }
  }

  export default CardOverlay;
