import { gql } from '@apollo/client';  
  
const Fetch_CURRENCY_QUERY = gql`
    query FetchCurrencyQuery {
      currencies {
        label
        symbol
        }
      }
    `


export default graphql(Fetch_CURRENCY_QUERY, {name: 'FetchCurrencyQuery'}) (Queries);
