import { React, PureComponent } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';


import Content from "./component/content/Content";
import Header from "./component/header/Header";


class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
        productCategory: ""
    }
    this.setLink = this.setLink.bind(this);
  }

  setLink() {
      this.setState({productCategory: this.props.link})
  }

  render() {

    return (
      <div className="App"> 
              <Router className="router">
              <Header setLink = {this.setLink} />
                <Routes>
                  <Route path={`product/${this.state.productCategory}`} element= {<Content cat = "all" />} />
                  {/* <Route path={`product/tech`} element= {<Content cat = "tech"/>} />
                  <Route path={`product/clothes`} element= {<Content cat = "clothes" />} /> */}
                </Routes>
            </Router>
      </div>
    );
  }

}

export default App;
