import { React, PureComponent  } from 'react';
import { gql } from '@apollo/client';
import { ApolloClient, InMemoryCache, ApolloProvider } from '@apollo/client';
import './styles/PDP.css';

const productSizes = [
    {
        size: "S"
    },
    {
        size: "M"
    },
    {
        size: "L"
    },
    {
        size: "XL"
    }

]
const client = new ApolloClient({
    uri:'http://localhost:4000/graphql',
    cache: new InMemoryCache()
  })

class PDP extends PureComponent {
    constructor(props) {
        super(props)
        this.state = {

        }

    }
    render() {
        const gallery = this.props.productDetails.gallery.map((index,item) => (
            <img alt={index} key={index} src={this.props.productDetails.gallery[item]} />
        ))

      return (
        <div className='pdp-container'>
            <div className='product-gallery'>
            {gallery}
            </div>
            <div className='product-image'>
                <img src={this.props.productDetails.gallery[0]} />
            </div>
            <div className='card-information'>
                <h1>{this.props.productDetails.name}</h1>
                <h2>{this.props.productDetails.brand}</h2>
                <h6>SIZE:</h6>
                <div className='sizes-container'>
                    <button>XS</button>
                    <button>S</button>
                    <button>M</button>
                    <button>L</button>
                </div>
                <h6>PRICE:</h6>
                <h6> $ {this.props.productDetails.prices[0].amount}</h6>
                <div className='add-to-card-container'>
                    <button onClick={ () => {this.addToCard(this.props.productDetails.id,
                         this.props.productDetails.name, 
                         this.props.productDetails.prices[0].amount,
                         "M",
                         this.props.productDetails.gallery[0]
                         )}} className={ this.props.productDetails.inStock === true ? "add-to-card" : "disabled-btn"}>
                        add to cart
                    </button>
                </div>
                <p>
                {(this.props.productDetails.description).replace(/<[^>]+>/g, '')}
                </p>
            </div>
            {console.log(this.state.cardProducts.id)}
        </div>
      ) 
    }
  }

  export default PDP;
