import { React, Component } from 'react';

import './styles/CardOverlay.css';
import { NavLink } from 'react-router-dom';

class CardOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = {
      cardOption: false,
      addedProducts: [],
      total:[0]
    }
  }
handleQuantity(index , opperator, price){

  var mainArray= this.props.toCart;
  var totalArray = this.state.total;
  if(opperator === "increment") {
    mainArray[index].qty += 1;
    totalArray.push(price);
    this.setState({total: totalArray})

  }
  if(opperator === "decrement") {
    if(mainArray[index].qty >= 1)
    mainArray[index].qty -= 1;
  }
  else mainArray[index].qty -= 0;
  
  this.setState({addedProducts: mainArray});
  }
calcTotal() {
  return(
    (this.state.total.reduce((res, i) => res +  i ))
  )
}
//render cardOverlay Items
renderItems() {
  return (
    this.props.toCart.length !== 0 ?
      this.props.toCart.map((item, index) => (
      <div key={index} className='card'>
        <div>
            <p>{item.name}</p>
            <p>{item.brand}</p>
            <p><span>{this.props.currency} </span>{item.prices.map((cur, r) => (
              this.props.currency === cur.currency.symbol ? (Math.round((cur.amount) * 100) / 100) : ""
            ))}</p>
            <div className='size-btn-container'>
                {isNaN(item.size) ? <button className="card-btn size-btn"> {item.size.charAt(0)}</button> : item.size !== "" ? <button className="card-btn size-btn"> {item.size}</button> : "" }
                {item.color !== "" ? <button className={`card-btn size-btn ${item.color}`}></button> : "" }
                {item.capacity !== "" ? <button className="card-btn size-btn"> {item.capacity}</button> : "" }
            </div>
        </div>
        <div className='card-con'>
        <div className='plus-btn'>
            <button  className='card-btn' onClick={()=>this.handleQuantity(index, "increment", item.prices[0].amount, item.qty)} >+</button>
            <span>{ item.qty }</span>
            <button className='card-btn' onClick={()=>this.handleQuantity(index, "decrement", item.price)}>-</button>
        </div>
            <img alt='' src={item.gallery[0]} />
        </div>
  </div>
    
  ))
    : <div className='empty-card'><p> Nothing here yet ! </p></div>
  )
}

    render() {
        const total = (this.state.total.reduce((res, i) => res +  i ));

      return (
          <div className='card-container'>
            <div onClick={() => this.props.closeCard(false)} className='card-overlay'>

            </div>

            <div className='fadein cover-overlay'>
                <div className='card-header'>
                    <p>My Bag. <span className='cart-qty'>{this.props.toCart.length} items</span></p>
                </div>
                <div className='items-container'>
                  {this.renderItems()}
                </div>
                <div className='card-footer'>
                  <div className='total-container'>
                    <p>total :</p>
                    <span>${this.calcTotal()}</span>
                  </div>
                  <div className='check-container'>
                      <NavLink to="cart"><button className='transparent'>view bag</button></NavLink>
                      <button className='green-white'>check out</button>
                  </div>
                </div>
             </div>
          </div>
      ) 
    }
  }

  export default CardOverlay;
