import { React, PureComponent  } from 'react';

import './styles/PDP.css';


class PDP extends PureComponent {
    constructor(props) {
      super(props);
      this.state = {
        gallery: [],
        topImg: "",
        pickedColor: false,
      }
    }

    componentDidMount() {
     this.setState({product: this.props.productDetails})
     this.setState({gallery: this.props.productDetails.gallery});
     this.setState({topImg: this.props.productDetails.gallery[0]})
    }

    //set picked product image to primary picture
    handleImagePick(imageLink) {
      this.setState({topImg: imageLink})
      
    }
    
    //render product gallery
    renderGallery() {
      return ( this.state.gallery.map((item, index) => (

        <img className='top-img' onClick={ () => this.handleImagePick(item)} alt="" key={index} src={item} />
      )  
    ))
    }

    renderAtrributes() {

      return (
          this.props.productDetails.attributes.map((attr, index) => (
              <div key={index}>
              <h6>{attr.name + ":"}</h6>
              <div className='attributes-container'>
                  
                    <div  className='attributes-list'>
                    {attr.items.map((items, i) => (
                      
                        attr.name === "Color" ?
                        <li key={i} onClick={ () => this.props.handleAttributColor(items.displayValue)} className={`${items.displayValue} attributes`} >

                        </li> 
                        : attr.name === "Size" ? 
                        <li key={i} onClick={ () => this.props.handleAttributSize(items.displayValue)} className="attributes" >
                        {items.displayValue}
                        </li> 
                        : attr.name === "Capacity" ? 
                        <li key={i}  onClick={ () => this.props.handleAttributCapacity(items.displayValue)} className="attributes" >
                        {items.displayValue}
                        </li>
                        : 
                        attr.name === "With USB 3 ports" ? 
                        <li key={i} className="attributes" >
                        {items.displayValue}
                        </li>
                        : 
                        attr.name === "Touch ID in keyboard" ? 
                        <li key={i} className="attributes" >
                        {items.displayValue}
                        </li>
                        : ""
                      
                      ))}
                    </div>
              </div>
            </div>
              
          ))
      )
    }

    render() {

      var props = this.props;
      return (
        <div className='pdp-container'>
            <div className='product-gallery'>

            {this.renderGallery()}

            </div>
            <div className='product-image'>
                <img alt='' src={this.state.topImg} />
            </div>
            <div className='card-information'>
                <h1>{props.productDetails.name}</h1>
                <h2>{props.productDetails.brand}</h2>

                <div className='sizes-container'>

                {this.renderAtrributes()}
                
                </div>

                <h6> {props.currency} {props.productDetails.prices.map((cur, index) => (
                  props.currency === cur.currency.symbol ? cur.amount : ""
                ))}</h6>
                <div className='add-to-card-container'>
                    <button onClick={ () => props.selectedProduct(props.productDetails)} className={ props.productDetails.inStock === true ? "add-to-card" : "disabled-btn" }>
                        add to cart
                    </button>
                </div>
                <p>
                {(props.productDetails.description).replace(/<[^>]+>/g, '')}
                </p>
            </div>
        </div>
      ) 
    }
  }

  export default PDP;
