import { React, PureComponent } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ApolloClient, InMemoryCache, ApolloProvider } from '@apollo/client';

import Content from "./component/content/Content";
import Header from "./component/header/Header";
import Home from "./component/home/Home";
import PDP from "./component/pdp/PDP";
import CardOverlay from "./component/card-overlay/CardOverlay";
import Cart from './component/cart/Cart';

const client = new ApolloClient({
  uri:'http://localhost:4000/graphql',
  cache: new InMemoryCache()
})


class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
        productCategory: "/all",
        productDetail: [],
        openClose: false,
    }
    this.setLink = this.setLink.bind(this);
    this.productDetails = this.productDetails.bind(this);
    this.openCloseCard = this.openCloseCard.bind(this);
    this.closeCard = this.closeCard.bind(this);
  }

  setLink(link) {
      this.setState({productCategory: link})
  }
  productDetails(details) {
    this.setState({productDetail: details})
  }
  openCloseCard(bool) {
    this.setState({openClose: !bool})
  }
  closeCard(close) {
    this.setState({openClose: close})
  }

  render() {
    return (
      <ApolloProvider client={client}>
      <div className="App"> 
              <Router className="router">
              <Header
                setLink = {this.setLink}
                openClose = {this.openCloseCard}
                />
                {this.state.openClose === true ? <CardOverlay closeCard = {this.closeCard} /> : ''}
                <Routes>
                  <Route path="/" exact={true} element= {<Home cat="welcome"
                  />} />
                  <Route path={this.state.productCategory}  element= {<Content cat = {(this.state.productCategory).substring(1)}  productDetail ={this.productDetails} />} />
                  <Route path={this.state.productCategory + "/" + this.state.productDetail.id}  element= {<PDP productDetails = {this.state.productDetail}  />} />
                  <Route path="cart"  element= {<Cart />} />
                </Routes>
            </Router>
      </div>
      </ApolloProvider>
    );
  }

}

export default App;
