import { React, PureComponent } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

import Content from "./component/content/Content";
import Header from "./component/header/Header";
import Home from "./component/home/Home";
import PDP from "./component/pdp/PDP";
import CardOverlay from "./component/card-overlay/CardOverlay";
import Cart from './component/cart/Cart';

class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
        active: false,
        pickedColor: "",
        pickedCapacity: "",
        pickedSize: "",
        currentCurrency: "$",
        productCategory: "all",
        productDetail: [],
        openClose: false,
        notAvailable: false,
        total:[0],
        qty: '',
        product: [

        ],
        nextProductId: 0,
    }

    this.changeCurrency = this.changeCurrency.bind(this);
    this.setLink = this.setLink.bind(this);
    this.productDetails = this.productDetails.bind(this);
    this.openCloseCard = this.openCloseCard.bind(this);
    this.closeCard = this.closeCard.bind(this);
    this.addToCart = this.addToCart.bind(this);
    this.onDelete = this.onDelete.bind(this);
    this.handleAttributColor = this.handleAttributColor.bind(this);
    this.handleAttributSize = this.handleAttributSize.bind(this);
    this.handleAttributCapacity = this.handleAttributCapacity.bind(this);
  }

  changeCurrency(curr) {
    this.setState({currentCurrency: curr})
  }

  setLink(link) {
    this.notAvailableItem(false)
    this.restAttributes();
    this.setState({productCategory: link})
  }

  productDetails(details) {
    this.setState({productDetail: details})
  }

  openCloseCard(bool) {
    this.setState({openClose: !bool})
  }
  closeCard(close) {
    this.setState({openClose: close})
  }
  notAvailableItem(bool) {
    this.setState({notAvailable: bool})
  }
  //Add customer product to card
  //param item object
  addToCart(item) {
    const { nextProductId,
            pickedColor,
            pickedSize,
            pickedCapacity,
            product } = this.state;

    this.restAttributes();
    if(item.inStock === true) {
      this.setState((prevState) => {
        const newItem = {...item,
            itemId: nextProductId, qty: 1,
            color: pickedColor,
            size: pickedSize,
            capacity: pickedCapacity
      };
        return {
          nextProductId: prevState.nextProductId + 1,
          product: [...product, newItem]
        }
      })
    }else this.notAvailableItem(true)

  }

  restAttributes() {
    this.setState({pickedColor: ""})
    this.setState({pickedSize: ""})
    this.setState({pickedCapacity: ""})
  }

  onDelete(index) {
    const { product } = this.state;
    var array = product.slice();
      array.splice(index, 1);
      this.setState({product: array})  

  }

  handleAttributColor(attr) {
    this.setState({pickedColor: attr})
  }

  handleAttributSize(attr) {
    this.setState({pickedSize: attr})
  }

  handleAttributCapacity(attr) {
    this.setState({pickedCapacity: attr})
  }
  handleQuantity(index , opperator, price){
    const { total, product } = this.state;
    // const { toCart } = this.props;

    var mainArray= product;
    var totalArray = total;

    if(opperator === "increment") {
      mainArray[index].qty += 1;
      totalArray.push(price);
      this.setState({total: totalArray})

    }
    if(opperator === "decrement") {
      if(mainArray[index].qty >= 1)
      mainArray[index].qty -= 1;
    }
    else mainArray[index].qty -= 0;

    this.setState({addedProducts: mainArray});
    }

  //calculate card items Total Price  
  calcTotal() {
    const { total } = this.state;
    return(
      (total.reduce((res, i) => res +  i ))
    )
  }
  render() {

    const { product,
            currentCurrency,
            openClose,
            productCategory,
            productDetail,
            notAvailable } = this.state;

    return (
      <div className="App"> 
              <Router className="router">
              <Header
                onChangeCurrency = {this.changeCurrency}
                setLink = {this.setLink}
                openClose = {this.openCloseCard}
                productCount = {product.length}
                currency = {currentCurrency}
                />
              {openClose === true ? <CardOverlay
                closeCard = {this.closeCard}
                // toCart = {product}
                currency = {currentCurrency}
                  /> : ''}
                <Routes>
                  <Route path="/" exact={true}
                   element= {<Home />}
                  />

                <Route path={productCategory}
                  element= {<Content cat = {productCategory} 
                  productDetail ={this.productDetails}
                  currency = {currentCurrency} />} 
                    />

                <Route path={productCategory + "/" + productDetail.id}  
                  element= {<PDP
                  productDetails = {productDetail}
                  selectedProduct = {this.addToCart}
                  currency = {currentCurrency}
                  handleAttributColor = {this.handleAttributColor}
                  handleAttributSize = {this.handleAttributSize}
                  handleAttributCapacity = {this.handleAttributCapacity}
                  notAvailable = {notAvailable}
                  />} 
                  />

                <Route path="cart"
                  element= {<Cart 
                  selectedProduct = {product}
                  currency = {currentCurrency}
                  onDelete = {this.onDelete}
                  />} 
                  />
                </Routes>
            </Router>
      </div>
    );
  }

}

export default App;
