import { React, PureComponent } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { ApolloClient, InMemoryCache, ApolloProvider } from '@apollo/client';

import Content from "./component/content/Content";
import Header from "./component/header/Header";
import PDP from "./component/pdp/PDP";

const client = new ApolloClient({
  uri:'http://localhost:4000/graphql',
  cache: new InMemoryCache()
})


class App extends PureComponent {
  constructor(props) {
    super(props);
    this.state = {
        productCategory: "all"
    }
    this.setLink = this.setLink.bind(this);
  }

  setLink(link) {
      this.setState({productCategory: link})
  }

  render() {
    return (
      <ApolloProvider client={client}>
      <div className="App"> 
              <Router className="router">
              <Header setLink = {this.setLink} />
                <Routes>
                <Route path="/" exact={true} element= {<Content cat = {(this.state.productCategory).substring(1)} />} />
                <Route path={this.state.productCategory}  element= {<Content cat = {(this.state.productCategory).substring(1)} />} />
                <Route path="/clothes/Nike%20Air%20Huarache%20Le"  element= {<PDP productId = "Nike%20Air%20Huarache%20Le" />} />
                </Routes>
            </Router>
      </div>
      </ApolloProvider>
    );
  }

}

export default App;
